import logo from './logo.svg';
import './App.css';

function App() {
  const divStyle = {
    backgroundColor: '#E5E3D3',
    width:'400px', // Note the quotes around '100px'
    height:'400px', // Note the quotes around '200px'
  };

  return (
    <center>
    <div id="box" style={divStyle}>
    <h2>Responsive Paragraph Word Counter</h2>
    <br></br>
    <textarea rows = "10" cols = "35"></textarea>
    <br></br><br></br>
    <span>Word Count:</span>
    </div>
    </center>
  );
}

export default App;
